mapper.select:
SELECT b.board_id, b.writer, b.title, b.views, b.created_at, b.modified_at, c.category_name, CASE WHEN EXISTS (SELECT 1 FROM file WHERE board_id = b.board_id AND is_deleted = 0) THEN 1 ELSE 0 END AS isAttached FROM board AS b JOIN category AS c ON b.category_id = c.category_id
AND b.created_at >= #{startDate}
AND b.created_at <= #{endDate}
AND b.category_id = #{category}
AND ( b.writer LIKE CONCAT('%', #{keyword}, '%') OR b.title LIKE CONCAT('%', #{keyword}, '%') OR b.content LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY b.board_id DESC;

SELECT COUNT(*) as totalBoardCount
FROM board AS b
    JOIN category AS c ON b.category_id = c.category_id



        AND b.created_at >= #{startDate}




        AND b.created_at <= #{endDate}



        AND b.category_id = #{category}


        AND (
        b.writer LIKE CONCAT('%', #{keyword}, '%') OR
        b.title LIKE CONCAT('%', #{keyword}, '%') OR
        b.content LIKE CONCAT('%', #{keyword}, '%')
        )

mapper.update:
UPDATE board
SET views = views + 1
WHERE board_id = #{boardId}
